$.ajax({
  setup,
  success: function (data) {
    getRisk(data);
  },
});

function getRisk(data) {
  console.log(data);
  $.ajax({
    url:
      'https://europe.grc1.com/iGRC/api/Risk/GetById_Summary?riskId=' +
      localStorage.getItem('risk id'),
    method: 'GET',
    headers: {
      Authorization: 'BEARER ' + data.access_token,
    },
    success: function (data2) {
      var r = data2;
      console.log(data2);
      $('.api-loader').hide();
      $('#risk-name').html(r.RiskName);
      $('#risk-business-unit').html(r.BusinessUnit);
      $('#risk-office').html(r.Office);
      $('#risk-consequence').html(r.Consequence);
      $('#risk-category').html(r.RiskCategory);
      $('#risk-subcategory').html(r.RiskSubcategory);
      $('#risk-description').html(r.RiskDescription);

      $('#current-impact-score').html(r.RegisterCurrentImpactScore);
      $('#current-likelihood-score').html(r.RegisterCurrentLikelihoodScore);
      $('#current-score').html(r.RegisterCurrentScore);

      $('#target-impact-score').html(r.RegisterTargetImpactScore);
      $('#target-likelihood-score').html(r.RegisterTargetLikelihoodScore);
      $('#target-target-score').html(r.RegisterTargetScore);

      $('#previous-impact-score').html(r.RegisterPreImpactScore);
      $('#previous-likelihood-score').html(r.RegisterPreLikelihoodScore);
      $('#previous-target-score').html(r.RegisterPreScore);

      $('#proximity').html(r.Proximity);

      $('#risk-owner-name').html(r.OwnerName);

      $('#score-update').html(r.LastResidualRiskScoreUpdate.split('T')[0]);
      $('#score-last-review-date').html(r.LastReviewDate.split('T')[0]);
      $('#score-next-review-date').html(r.NewReviewDate);
    },
    error: function (e) {
      console.log('error');
    },
  });
}

$('#print').click(() => {
  $('#print').printThis();
});
