$(document).ready(() => {
  GetRisksValues($('#risks-list button:first'));
});

function getData(e) {
  GetRisksValues(e);
}

function GetRisksValues(e) {
  const btn = e;
  e !== null ? (e = $(e).data('risk-numbers')) : null;
  $('#number-of-risks-red').html(e.number_red);
  $('#percentage-of-risks-red').html(e.percentage_red);
  $('#number-of-risks-blue').html(e.number_blue);
  $('#number-of-risks-blue').html(e.percentage_blue);
  $('#risks-trends-1').html(e.trends_1);
  $('#risks-trends-2').html(e.trends_2);
  $('#risks-trends-3').html(e.trends_3);
  $(`#risks-list button`).not($(btn)).addClass('btn-secondary').removeClass('btn-danger')
  $(btn).addClass('btn-danger')

}
