export function switchWindows(buttons, windows) {


  function showActiveWindow() {

    windows.forEach(element => {
      $(element).hide()
    });

    buttons.forEach(element => $(element).removeClass('button-nav-active'))

    for (let i = 0; i < windows.length; i++) {
      if (!location.hash) {
        $(windows[0]).show();
        $(buttons[0]).addClass('button-nav-active');
      }
      else if (location.hash === windows[i]) {
        $(windows[i]).show();
        $(buttons[i]).addClass('button-nav-active');
      }
    }

  }

  function preventScrolling() {
    var x = window.pageXOffset,
      y = window.pageYOffset;
    $(window).one('scroll', function () {
      window.scrollTo(x, y);
    });
  }


  $(document).ready(() => {
    showActiveWindow();
  })

  window.addEventListener('hashchange', () => {
    showActiveWindow();
  });

  for (let i = 0; i < buttons.length; i++) {
    $(buttons[i]).click(() => {
      preventScrolling();
      location.hash = windows[i]
    });
  }
}





