//Switching between graph and risk table
const esg_graph_btn = $('#esg-graph-btn'),
  disclosure_metrics_btn = $('#disclosure-metrics-btn'),
  esg_graph = $('#esg-graph'),
  disclosure_metrics = $('#disclosure-metrics'),
  standards_reporting = $('#standards-reporting'),
  activeButton = '<style>';
$(document).ready(() => {
  disclosure_metrics.hide(), esg_graph_btn.toggleClass('button-nav-active');
}),
  esg_graph_btn.click(() => {
    disclosure_metrics.hide(),
      esg_graph.show(),
      standards_reporting.show(),
      esg_graph_btn.toggleClass('button-nav-active'),
      disclosure_metrics_btn.removeClass('button-nav-active');
  }),
  disclosure_metrics_btn.click(() => {
    disclosure_metrics.show(),
      esg_graph.hide(),
      standards_reporting.hide(),
      disclosure_metrics_btn.toggleClass('button-nav-active'),
      esg_graph_btn.removeClass('button-nav-active');
  });
