//Switching between graph and risk table
const materiality_btn = $('#materiality-btn'),
  key_data_btn = $('#key-data-btn'),
  key_data = $('#key-data'),
  materiality = $('#materiality'),
  managment = $('#managment');
 
$(document).ready(() => {
  materiality.hide(), key_data_btn.toggleClass('button-nav-active');
}),
  materiality_btn.click(() => {
    key_data.hide(),
      materiality.show(),
      materiality_btn.toggleClass('button-nav-active'),
      key_data_btn.removeClass('button-nav-active');
  }),
 key_data_btn.click(() => {
   key_data.show(),
      materiality.hide(),
     key_data_btn.toggleClass('button-nav-active'),
     materiality_btn.removeClass('button-nav-active');
  });
