var years = [
  '2018',
  '2019',
  '2020',
  '2021',
  '2022',
];
var percentage = [45.8, 29.2, 8.3, 8.3, 8.3];
var percentage2 = [11, 23, 45, 11, 90];

var datasets = [
  {
    label: 'data 1',
    data: percentage,
    borderColor:'blue',
    backgroundColor:'blue',
  
  },
  {
    label: 'data 2',
    data: percentage2,
    backgroundColor:'red',
    borderColor:'red',
   
  },
]
var options = {
  legend: {
    display: 'true',
    position: 'right',
  },
  plugins: {
    labels: {
      fontColor: 'black',
      fontWeight: 'bold',
      render: 'value',
    },
  },
  
}


var chart = new Chart('analytics-risks-chart', {
  type: 'line',
  data: {
    labels: years,
    datasets: datasets
  },
  options: options
});

$('#risks-chart-type-select').on('change', () => {
  chart.destroy();
  chart = new Chart('analytics-risks-chart', {
    type: $('#risks-chart-type-select').val(),
    data: {
      labels: years,
      datasets: datasets
    },
    options: options
  });
})
