var years = [
  '2018',
  '2019',
  '2020',
  '2021',
  '2022',
];
var percentage = [45.8, 29.2, 8.3, 8.3, 8.3];
var percentage2 = [11, 23, 45, 11, 90];

var datasets = [
  {
    label: 'implemented',
    data: percentage,
  
  },
  {
    label: 'non implemented',
    data: percentage2,
   
  },
]

var options = {
  legend: {
    display: 'true',
    position: 'right',
  },
  plugins: {
    labels: {
      fontColor: 'black',
      fontWeight: 'bold',
      render: 'value',
    },
  },
  scales: {
    xAxes: [
      {
        ticks: {
          fontFamily: "Verdana",
          fontSize: 11,

        },
      },
    ],
  },
}


var chart = new Chart('regional-performance-bar', {
  type: 'line',
  data: {
    labels: years,
    datasets: datasets
  },
  options: options
});

$('#chartType').on('change', () => {
  chart.destroy();
  chart = new Chart('regional-performance-bar', {
    type: $('#chartType').val(),
    data: {
      labels: years,
      datasets: datasets
    },
    options: options
  });
})
