const reduceValues = (val) => {
  return val.reduce((a, b) => a + b);
};
var lineChart = [
  reduceValues(values.v1),
  reduceValues(values.v2),
  reduceValues(values.v3),
];

function getRandom() {
  var arr = [];

  while (arr.length < 3) {
    let rnd = Math.floor(Math.random() * 100) + 10;

    arr.push(rnd);
  }

  return arr;
}

