
var values = [20, 70, 50,25];
var values2 = [100, 100, 100,100];

new Chart('total-practices', {
  type: 'bar',
  data: {
    labels: ['Policy', 'Process', 'Decision Making Structures','Best practice Mechanisms'],
    datasets: [
      {
        data: values,
        backgroundColor: '#42d1f5',
        borderWidth: 1,
      
      },
      {
        data: values2,
        backgroundColor: '#42a7f5',
        borderWidth: 1,
      },
    ],
  },
  options: {
    scales: {
      xAxes: [{ stacked: true,
        ticks: {
          fontFamily: "Verdana",
          fontSize:10.5,
          
        } },
        
      
      ],
      yAxes: [
        {
          stacked: false,
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },

    plugins: {
      labels: {
         fontColor: '#567324', 
         fontFamily: "Arial Black",
     
        textMargin: -25,
        render: function (args) {
          if (args.value >= 100) {
            return '';
          } else return args.value + '%';
        },
      },
    },
  },
});

