var labels = ['Controlled', 'Partial', 'No Information'];
var percentage = [45.8, 29.2, 8.3];

var datasets = [
  {
    label: labels[0],
    data: percentage,

    backgroundColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)',
    ],
  },

];
var options = {
  legend: {
    display: 'true',
    position: 'right',
  },
  plugins: {
    labels: {
      fontColor: 'black',
      render: 'value',
    },
  },
};

var chart = new Chart('data-collection-chart', {
  type: 'doughnut',
  data: {
    labels: labels,
    datasets: datasets,
  },
  options: options,
});

