var labels = ['Controlled', 'Partial', 'No Information'];
var percentage = [45.8, 29.2, 8.3, 8.3, 8.3];
var percentage2 = [11, 23, 45, 11, 90];

var datasets = [
  {
    label: labels[0],
    data: percentage,

    backgroundColor: 'blue',
  },
  {
    label: labels[1],
    data: percentage2,
    backgroundColor: 'red',
  },
  {
    label: labels[2],
    data: percentage2,
    backgroundColor: 'green',
  },
];
var options = {
  legend: {
    display: 'true',
    position: 'right',
  },
  plugins: {
    labels: {
      fontColor: 'black',
      render: 'value',
    },
  },
};

var chart = new Chart('data-collection-chart', {
  type: 'doughnut',
  data: {
    labels: labels,
    datasets: datasets,
  },
  options: options,
});

