var labels = ['Policy', 'Process', 'Decision-making'];
var values = [20, 70, 50];
var values2 = [100, 100, 100];

new Chart('total-practices', {
  type: 'bar',
  data: {
    labels: ['Policy', 'Process', 'Decision-making'],
    datasets: [
      {
        data: values,
        backgroundColor: '#93e362',
        borderWidth: 1,
      },
      {
        data: values2,
        backgroundColor: '#6aa600',
        borderWidth: 1,
      },
    ],
  },
  options: {
    scales: {
      xAxes: [{ stacked: true }],
      yAxes: [
        {
          stacked: false,
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },

    plugins: {
      labels: {
        fontColor: '#567324',
        fontSize: 20,
        textMargin: -25,
        render: function (args) {
          if (args.value >= 100) {
            return '';
          } else return args.value + '%';
        },
      },
    },
  },
});
