$(document).ready(() => {
  $('#checklist-container').click((e) => {
    e.stopPropagation();
  });
  const check = [
    '#cards-check',
    '#performance-check',
    '#readiness-check',
    '#risks-check',
    '#locations-check',
    '#todo-check',
    '#team-tasks-check',
  ];

  const targets = [
    '#card-grid',
    '#_treemap',
    '#readiness',
    '#risks',
    '#locations',
    '#todo',
    '#team-tasks',
  ];

  for (let i = 0; i < check.length; i++) {
    checkbox(check[i], targets[i]);
  }
});

function checkbox(check, target) {
  $(`${check}:checkbox`).change(function () {
    if ($(this).is(':checked')) {
      $(target).show();
      if (check === '#cards-check') {
        $('#manage-cards-btn').show();
      }
     
    } else {
      $(target).hide();
      if (check === '#cards-check') {
        $('#manage-cards-btn').hide();
      }
    }
  });
}
