function createTable(id){
    $(id).DataTable({
      dom: 'Bfrtp',

      language: {
        paginate: {
          previous: "<i class='fas fa-angle-left'>",
          next: "<i class='fas fa-angle-right'>"
        }
      },
      buttons: [
        {
          extend: 'collection',
          text: 'Show columns',
          columns: '.colhead',
          className: 'btn-outline-primary btn-sm ',
          buttons: ['columnsVisibility'],
          visibility: !0,
        },
        {
          extend: 'print',
          className: 'btn-datatable btn-sm',
        },
        {
          extend: 'pdf',
          className: 'btn-datatable btn-sm',
        },
        {
          extend: 'excel',
          className: 'btn-datatable btn-sm',
        },
        {
          extend: 'copy',
          className: 'btn-datatable btn-sm',
        },
      ],
    });
  }