class DT {
  constructor(id) {
    var table = {
      dom: 'Bfrltp',
      ordering: false,
      language: {
        paginate: {
          previous: "<i class='fas fa-angle-left'>",
          next: "<i class='fas fa-angle-right'>",
        },
        lengthMenu: 'Display _MENU_',
      },
      buttons: [
        {
          extend: 'collection',
          text: 'Show columns',
          columns: '.colhead',
          className: 'btn-outline-primary btn-sm ',
          buttons: ['columnsVisibility'],
          visibility: !0,
        },
        {
          extend: 'print',
          className: 'btn-datatable btn-sm',
        },
        {
          extend: 'pdf',
          className: 'btn-datatable btn-sm',
        },
        {
          extend: 'excel',
          className: 'btn-datatable btn-sm',
        },
        {
          extend: 'copy',
          className: 'btn-datatable btn-sm',
        },
      ],
    };
    this.id = $(id).DataTable(table);

    $(id).on('click', 'thead th', function (cs) {
      
      $(id).DataTable().column(this).visible(false);
    });
  }
}

