var DatatableSasb = (function () {
  function b(a) {
    a.on('init.dt', function () {
      $('.dt-buttons .btn')
        .removeClass('btn-secondary')
        .addClass('btn-sm btn-default');
    }).DataTable({
      lengthMenu: [
        [10, 25, 50, -1],
        [10, 25, 50, 'All'],
    ],
      stateSave: !0,
      stateDuration: 86400,
      scrollY: 400,
      dom: 'Bfrtip',
      language: {
        paginate: {
          previous: '<',
          next: '>',
        },
      },
    
      buttons: [
        {
          extend: 'collection',
          text: 'Show columns',
          columns: '.colhead',
          className: 'btn-outline-primary',
          buttons: ['columnsVisibility'],
          visibility: !0,
        },
        'copy',
        { extend: 'print', exportOptions: { columns: ':visible' } },
        {
          extend: 'pdf',
          pageSize: 'A4',
          orientation: 'landscape',
          exportOptions: { columns: ':visible' },
        },
        'excel',
      ],
    });
  }
  var a = $('#disclosure-datatable');
  a.length && b(a);
})();
