$(document).ready(() => {
  var el =` <li class="expandable">
  <a
  class="nav-link"
  href="#metric-nav"
  data-toggle="collapse"
  role="button"
  aria-expanded="false"
  >
    <span class="sidenav-mini-icon"> I </span>
    <span class="sidenav-normal">Individual Metric </span>
  </a>

  <div class="collapse" id="metric-nav">
    <ul class="list-unstyled">
      <li>
        <a
          class="nav-link"
          href="#overview"
        >
          <span class="sidenav-mini-icon"> -o </span>
          <span class="sidenav-normal"> -overview </span>
        </a>
      </li>
      <li>
        <a
          class="nav-link"
          href="#materiality"
        >
          <span class="sidenav-mini-icon"> -m </span>
          <span class="sidenav-normal"> -materiality </span>
        </a>
      </li>
      <li>
        <a
          class="nav-link"
          href="#management"
        >
          <span class="sidenav-mini-icon"> -m </span>
          <span class="sidenav-normal"> -management </span>
        </a>
      </li>
      <li>
        <a
          class="nav-link"
          href="#breakdown"
        >
          <span class="sidenav-mini-icon"> -b </span>
          <span class="sidenav-normal"> -breakdown </span>
        </a>
      </li>
    </ul>
  </div>
</li>

`;

  $('#metrics-nav').append(el);
  getNames()
});

function getNames() {
  var title = localStorage.getItem('metric title');
  var score = localStorage.getItem('metric raw-score');
  var unit = localStorage.getItem('metric unit');
  var img =  window.localStorage.getItem('metric img');
  $('.metric-title').html(title);
  $('.raw-score').html(score);
  $('.unit').html(unit);
  $('.metric-img').attr('src',img);
}
