function redirectToMetric(e) {
  const raw_score = $(e).next().text();
  const unit = $(e).next().next().text();
  const trend = $(e).next().next().next().text();
  
  window.localStorage.setItem('metric title', e.innerHTML);
  window.localStorage.setItem('metric raw-score', raw_score);
  window.localStorage.setItem('metric unit', unit);
  window.localStorage.setItem('metric trend', trend);
}
