$(document).ready(() => {
  var childs = $('#notifications-list a').length;
  if (childs <= 0) {
    $('#notifications-badge').addClass('d-none');
    $('#notifications-badge-expanded').html('0');
  }
  $('#notifications-badge').html(childs);
  $('#notifications-badge-expanded').html(childs);
});

$('#clear-all-notifications-btn').click(() => {
  $('#notifications-list').empty();
  
    $('#notifications-badge').addClass('d-none');
    $('#notifications-badge-expanded').html('0');
});

