$('#navbar-search-main').on('submit', function (e) {
  e.preventDefault();
  var val = $('#navbar-search-main input').val();

  const filt = pages.filter((x) => {
    return x.includes(val);
  });
  if (filt.length>=1) {
    const path = window.location.origin + '/dashboard/' + filt[0];
    window.location.replace(path);
  } else {
    $('#navbar-search-main input').val('no results found');
  }
});

const pages = [
  'home.html#cards',
  'company/disclosure.html#esg-graph',
  'company/disclosure.html#disclosure-metrics',
  'company/risk-overview.html#risk-overview',
  'company/risk-overview.html#risk-items',
  'company/metrics.html#key-data',
  'company/metrics.html#materiality',
  'company/metrics.html#managment',
  'company/metrics.html#breakdown',
  'frameworks/sasb.html',
  'reports/reports.html', 
  'actions/actions.html#calendar',
  'actions/actions.html#todo',
  'settings/settings.html',
  'account/profile.html',
];
