
$('#submit-btn').click(() => {
  $.ajax({
    url: 'https://emailer/sendEmail.php',
    method: "POST",
    data: {
      name: $('#name').val(),
      email: $('#email').val(),
      message: $('#message').val(),
    },
    success: function (data) {
        console.log(data)
      $('#name').val('');
      $('#email').val('');
      $('#message').val('');
    },
    error: function (e) {
      console.log(e);
      $('#name').val('');
      $('#email').val('');
      $('#message').val('');
    },
  });
});
