$('#submit-btn').click(()=>{
    $.ajax({
        url: "/dashboard/assets/php/sendEmail.php",
        data:{
            name: $("#name").val(),
            email: $("#email").val(),
            message: $("#message").val()
        },
        success: function(data){
            console.log('success')
        },
        error: function(e){
            console.log(e)
        }
    })
})