$('input[type="checkbox"]').click(function () {
  if ($(this).is(':checked')) {
    switch (this.id) {
      case 'tooltips-check':
        localStorage.setItem('tooltips_on', true);

        break;
      default:
        break;
    }
  } else {
    switch (this.id) {
      case 'tooltips-check':
        localStorage.setItem('tooltips_on', false);
        break;
      default:
        break;
    }
  }
});

$(document).ready(()=>{
    var checkes = JSON.parse(localStorage.getItem('tooltips_on'));

    if(checkes){
        $('#tooltips-check').attr("checked",true);
    }
})
