
$('input[type="checkbox"]').click(function () {
  if ($(this).is(':checked')) {
    switch (this.id) {
      case 'tooltips-check':
        localStorage.setItem('tooltips_on', true);
        $('#tooltips-check').attr('checked', true)
        break;
      case 'notifications-check':
        localStorage.setItem('notifications_on', true);
        $('#notifications-check').attr('checked', true);
        break;
    }
  } else {
    switch (this.id) {
      case 'tooltips-check':
        localStorage.setItem('tooltips_on', false);
        $('#tooltips-check').attr('checked', true)
        break;
      case 'notifications-check':
        localStorage.setItem('notifications_on', false);
        $('#notifications-check').attr('checked', false);
        break;
    }
  }
});

$(document).ready(() => {
  var tooltips = JSON.parse(localStorage.getItem('tooltips_on'));

  if(tooltips===null || tooltips) {
  $('#tooltips-check').attr('checked', true)

  }
  else{
    $('#tooltips-check').attr('checked', false)
  }
  var notifications = JSON.parse(localStorage.getItem('notifications_on'));
  if (notifications) {
    $('#notifications-check').attr('checked', true);
  }
});

$("#reset-settings-btn").click(()=>{
  localStorage.clear()
})
