var checked = JSON.parse(localStorage.getItem('tooltips_on'));

$(document).ready(() => {
  if (checked || checked === null) {
    $('[data-toggle="tooltip"]').on('click', function () {
      $(this).tooltip('hide');
    });


  } else {
    $('[data-toggle="tooltip"]').mouseover(function () {
      $(this).tooltip('hide');
    });
  }
});
