//Zoom
var zoomed = true;

$('#zoom-btn').click(() => {
  zoomed = !zoomed;
  if (!zoomed) {
    $('#widgets-grid').animate({ zoom: 0.7 }, 400);
    $('.movable-cards').removeClass('col-6');
    $('.movable-cards').addClass('col-4');
    $('#zoom-btn').html('Zoom In');
  } else {
    $('#widgets-grid').animate({ zoom: 1 }, 400);
    $('.movable-cards').removeClass('col-4');
    $('.movable-cards').addClass('col-6');

    $('#zoom-btn').html('Zoom Out');
  }
});
